class Solution(object):
    def majorityElement(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        return self.divide_conquer(nums)

    def divide_conquer(self, nums):
    	if len(nums) == 1:
    		return nums[0]

    	mid = len(nums) / 2
    	a1 = self.divide_conquer(nums[:mid])
    	a2 = self.divide_conquer(nums[mid:])
    	if a1 is None and a2 is None:
    		return None
    	if a1 and a2 and a1 == a2:
    		return a1
    	else:
    		cnt1 = nums.count(a1)
    		cnt2 = nums.count(a2)
    		if cnt1 > cnt2:
    			return a1
    		else:
    			return a2



